import React from "react"

interface Props {
	title: string
	calories: number
	img: string
	ingredients: []
}

const Recipe: React.FC<Props> = ({ title, calories, img, ingredients }) => {
	console.log(ingredients)

	return (
		<>
			<h1>{title}</h1>
			<ol>
				{ingredients.map(({ text }) => (
					<li>{text}</li>
				))}
			</ol>
			<p>Calories: {Math.floor(calories)}</p>
			<img src={img} alt='' />
		</>
	)
}

export default Recipe
