import React, { useState, useCallback } from "react"
import styled from "styled-components"

interface Props {
    title: string
    calories: number
    img: string
    ingredients: [{ text: string; weight: number }]
}

const Recipe: React.FC<Props> = ({ title, calories, img, ingredients }) => {
    const [showModal, setShowModal] = useState<boolean>(false)
    const handleModal = useCallback((show: boolean) => {
        setShowModal(show)
    }, [])

    return (
        <>
            <RecipeItem>
                <Title>{title}</Title>
                <Wrapper>
                    <Figure>
                        <img
                            src={img}
                            alt={title}
                            onClick={() => handleModal(true)}
                        />
                    </Figure>
                    <div>Calories: {Math.floor(calories)}</div>
                </Wrapper>

                <ul className='recipe__ingredients'>
                    {ingredients.map(({ text }, i) => (
                        <li key={i} className='recipe__ingredients-item'>
                            {text}
                        </li>
                    ))}
                </ul>
            </RecipeItem>
            <div
                className={`modal ${showModal ? "show" : "hide"}`}
                onClick={() => handleModal(false)}>
                <div className='modal__content'>
                    <img src={img} alt={title} className='modal__image' />
                </div>
            </div>
        </>
    )
}

const RecipeItem = styled.li`
    list-style-type: none;
    list-style-position: inside;
    display: grid;
    justify-content: space-evenly;
    background: #fff;
    padding: 1rem;
    box-shadow: 0.025rem 0.025rem 0.05rem 0 rgba(80, 80, 80, 0.295);
`

const Title = styled.h5`
    text-align: center;
    font-size: 1.25rem;
`

const Wrapper = styled.div`
    display: flex;
    justify-content: space-evenly;
    align-items: center;
`

const Figure = styled.figure`
    border-radius: 50%;
    height: 5rem;
    width: 5rem;
    margin: 0.5rem auto;

    img {
        height: 100%;
        width: 100%;
        border-radius: 50%;
    }

    div {
        margin: auto;
    }
`

export default Recipe
