import React, { useState, useEffect, useCallback } from "react"
import Recipe from "./Recipe"
import gif from "./loadinfo.net.gif"

interface Props {}

const App: React.FC<Props> = () => {
    const APP_ID = process.env.REACT_APP_API_ID
    const APP_KEY = process.env.REACT_APP_KEY

    const [recipes, setRecipes] = useState<any[]>([])
    const [search, setSearch] = useState<string>("chicken")
    const [loading, setLoading] = useState<boolean>(false)
    // const [query, setQuery] = useState<string>("chicken")

    const getData: any = useCallback(
        async (text: string) => {
            try {
                setLoading(true)
                const index = Math.floor(Math.random() * 100)

                const response = await fetch(
                    `https://api.edamam.com/search?q=${text}&app_id=${APP_ID}&app_key=${APP_KEY}&from=${index}`
                )
                const data = await response.json()
                setRecipes(prev => {
                    return [...prev, ...data.hits]
                })
                setLoading(false)
            } catch (e) {
                const response = await fetch(
                    `https://api.edamam.com/search?q=${text}&app_id=${APP_ID}&app_key=${APP_KEY}`
                )
                const data = await response.json()
                setRecipes(prev => {
                    return [...prev, ...data.hits]
                })
                setLoading(false)
            }
        },
        [APP_ID, APP_KEY]
    )

    useEffect(() => {
        getData(search)
    }, [])

    const handleChange = useCallback(e => {
        setSearch(e.target.value)
    }, [])

    const handleSubmit = useCallback(
        e => {
            setRecipes([])
            e.preventDefault()
            getData(search)
            setSearch("")
        },
        [getData, search]
    )

    const loadMore = () => {
        getData(search)
    }

    return (
        <>
            <div className='loading'>
                <img
                    src={gif}
                    alt=''
                    className='loading__image'
                    style={{ display: loading ? "block" : "none" }}
                />
            </div>
            <form className='search' onSubmit={handleSubmit}>
                <input
                    type='text'
                    className='search__bar'
                    value={search}
                    onChange={handleChange}
                />
                <button type='submit' className='search__btn'>
                    Search
                </button>
            </form>
            <ul className='recipe'>
                {recipes.map(({ recipe }: any, i: number) => (
                    <Recipe
                        key={i}
                        title={recipe.label}
                        calories={recipe.calories}
                        img={recipe.image}
                        ingredients={recipe.ingredients}
                    />
                ))}
            </ul>
            <div className='more' onClick={loadMore}></div>
            <a href='#root' className='scroll-up'>
                â†‘
            </a>
        </>
    )
}

export default App
